document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".home-page__box"),t=[].slice.call(e);if("IntersectionObserver"in window){let e=new IntersectionObserver(function(e,t){e.forEach(function(e){if(e.isIntersecting){let t=e.target.getAttribute("data-bg");e.target.firstElementChild.classList.add("home-page--"+t+"-bg")}})});t.forEach(function(t){e.observe(t)})}else e.forEach(e=>{let t=e.getAttribute("data-bg");e.firstElementChild.classList.add("home-page--"+t+"-bg")})});const medium=600,large=992,fontSize=24,cards=document.querySelectorAll(".card");let resizeDelay;function resize(){cards.forEach(e=>{resizeFont(e),resizeTextBox(e),e.addEventListener("mouseenter",()=>{resizeTextBoxOver(e)}),e.addEventListener("mouseleave",()=>{resizeTextBox(e)})})}function resizeFont(e){let t=e.clientWidth;t>=large?(setFontSize(e.querySelector(".card__title"),2.3),setFontSize(e.querySelector(".card__sub-title"),1.15),setFontSize(e.querySelector(".card__text"),1.15)):t>=medium?(setFontSize(e.querySelector(".card__title"),1.6),setFontSize(e.querySelector(".card__sub-title"),1.6*.7),setFontSize(e.querySelector(".card__text"),.96)):(setFontSize(e.querySelector(".card__title"),1),setFontSize(e.querySelector(".card__sub-title"),.7),setFontSize(e.querySelector(".card__text"),.65))}function setFontSize(e,t){e.style.fontSize=(fontSize*t).toString().concat("px")}function resizeTextBox(e){let t=e.clientWidth;const r=e.querySelector(".card__text");r.style.top=t>=large?"50%":t>=medium?"40%":"35%"}function resizeTextBoxOver(e){let t=e.clientWidth;const r=e.querySelector(".card__text");r.style.top=t>=large?"35%":t>=medium?"30%":"25%"}window.addEventListener("resize",e=>{clearTimeout(resizeDelay),resizeDelay=setTimeout(resize,5)}),resize();